import { Component } from '@angular/core';
import { Router } from '@angular/router';
import {Client} from "../services/client";
import {HttpErrorResponse} from "@angular/common/http";
import {ClientService} from "../services/client.service";
import {NgForm} from "@angular/forms";

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  clients: Client[] = [];

  constructor(private router: Router,
              private clientService: ClientService){ }

  ngOnInit() {
    this.getClients();
  }
  public clickOnRegisterAccount(setName: String, setEmail : String, setAge : String, setPhone: String,
                                setPassword: String): void{

    let ok: number = 0;
    if(setName != "" && setAge != "" && setPassword != "")
      for(let client of this.clients){
        if (client.email == setEmail || client.phone == setPhone)
          ok = 1;
      }
    if(ok == 0) {
      const client: Client = new class implements Client {
        age: number = Number(setAge);
        clientCode: string = '111';
        email: string = String(setEmail);
        id: number = 1;
        imageUrl: string = String();
        name: string = String(setName);
        password: string = String(setPassword);
        phone: string = String(setPhone);
      };
      this.clientService.addClient(client).subscribe(
        (response: Client) => {
          console.log(response);
          this.getClients();
        },
        (error: HttpErrorResponse) =>{
          alert(error.message);
        }
      );
      this.router.navigate([`login-component`]);
    }
    else
      alert("Exist an account with this email/phone");
  }

  public getClients(){
    this.clientService.getClients().subscribe(
      (response: Client[]) => {
        this.clients = response;
      },
      (error: HttpErrorResponse) =>{
        alert(error.message);
      }
    );
  }
}

<section class="image vh-100 bg-image">
  <div class="mask d-flex align-items-center h-100 gradient-custom-3">
    <div class="container h-100">
      <div class="row d-flex justify-content-center align-items-center h-100">
        <div class="col-12 col-md-9 col-lg-7 col-xl-6">
          <div class="card" style="border-radius: 15px;">
            <div class="card-body p-3">
              <h2 class="text-uppercase text-center mb-3">Welcome!</h2>
              <form>
                <div class="form-outline mb-0">
                  <label class="form-label" for="form3Example1cg">Your Name</label>
                  <input type="name" id="form3Example1cg" class="form-control form-control-lg" placeholder="Name"
                         ngModel required #name="ngModel" [ngModelOptions]="{standalone: true}"/>
                </div>

                <div class="form-outline mb-0">
                  <label class="form-label" for="form3Example2cg">Your Email</label>
                  <input type="email" id="form3Example2cg" class="form-control form-control-lg" placeholder="Email"
                         ngModel required #email="ngModel" [ngModelOptions]="{standalone: true}"/>
                </div>

                <div class="form-outline mb-0">
                  <label class="form-label" for="form3Example3cg">Your Phone</label>
                  <input type="phone" id="form3Example3cg" class="form-control form-control-lg" placeholder="Phone"
                         ngModel required #phone="ngModel" [ngModelOptions]="{standalone: true}"/>
                </div>

                <div class="form-outline mb-0">
                  <label class="form-label" for="form3Example4cg">Your Age</label>
                  <input type="age" id="form3Example4cg" class="form-control form-control-lg" placeholder="Age"
                         ngModel required #age="ngModel" [ngModelOptions]="{standalone: true}"/>
                </div>

                <div class="form-outline mb-0">
                  <label class="form-label" for="form3Example5cg">Type Password</label>
                  <input type="password" id="form3Example5cg" class="form-control form-control-lg" placeholder="Password"
                         ngModel required #password="ngModel" [ngModelOptions]="{standalone: true}"/>
                </div>
                <select name="test[]" size="1" multiple>
                  <option>
                  <option>456
                  <option>789
                </select>
                <div>
                  <label></label>
                </div>


                <div class="d-flex justify-content-center">
                  <button (click)="clickOnRegisterAccount( name.value, email.value, age.value, phone.value, password.value)"
                          type="button"
                          class="btn btn-success btn-block btn-lg gradient-custom-4 text-body">REGISTER</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
<router-outlet></router-outlet>
