import {Component, OnInit} from '@angular/core';
import {Doctor} from "../services/doctor";
import {DoctorService} from "../services/doctor.service";
import {HttpErrorResponse} from "@angular/common/http";
import {ProfessionService} from "../services/profession.service";
import {Profession} from "../services/profession";

@Component({
  selector: 'app-doctors',
  templateUrl: './doctors.component.html',
  styleUrls: ['./doctors.component.css']
})
export class DoctorsComponent implements OnInit{
  public doctors: Doctor[] | undefined;
  public professions: Profession[] | undefined;
  public currentDoctor: Doctor = {
    id: 0,
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    password: "",
    imageUrl: "",
    doctorCode: "",
    professions: [],
  };

  constructor(private doctorService: DoctorService, private professionService: ProfessionService){
    this.doctorService = doctorService;
    this.professionService = professionService;
  }

  ngOnInit(){
    this.getDoctors();
    this.getAllProfessions();
  }

  public clickOnSeeMoreButton(doctor: Doctor): void{
    this.currentDoctor = doctor;
  }
  public myfct(){

  }

  public getDoctors(): void {
    this.doctorService.getDoctors().subscribe(
      (response: Doctor[]) => {
        this.doctors = response;
        },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
      )
  }

  public getAllProfessions(): void {
    this.professionService.getProfession().subscribe(
      (response: Profession[]) => {
        this.professions = response;
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
    )
  }

  private getCurrentDoctor() {
    this.doctorService.getDoctorById(1).subscribe(
      (response: Doctor) => {
        this.currentDoctor = response;
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
    )
  }
}
