import {Component, OnInit} from '@angular/core';
import {Doctor} from "../services/doctor";
import {DoctorService} from "../services/doctor.service";
import {HttpErrorResponse} from "@angular/common/http";
import {Observable} from "rxjs";

@Component({
  selector: 'app-doctors',
  templateUrl: './doctors.component.html',
  styleUrls: ['./doctors.component.css']
})
export class DoctorsComponent implements OnInit{
  public doctors: Doctor[] | undefined;
  public currentDoctor: Doctor | undefined;
  public doctorId: number | undefined = 1;
  public doctorFirstName: string | undefined = "";
  public doctorLastName: string | undefined = "";
  public doctorEmail: string | undefined = "";
  public doctorPhone: string | undefined = "";

  constructor(private doctorService: DoctorService){
    this.doctorService = doctorService;
  }

  ngOnInit(){
    this.getDoctors();
    /*
    this.doctorId = this.currentDoctor?.id;
    this.doctorFirstName = this.currentDoctor?.firstName;
    this.doctorLastName = this.currentDoctor?.lastName;
    this.doctorEmail = this.currentDoctor?.email
    this.doctorPhone = this.currentDoctor?.phone;
     */
  }

  public clickOnSeeMoreButton(doctor: Doctor): void{
    this.currentDoctor = doctor;
    console.log(doctor.professions);
  }

  public getDoctors(): void {
    this.doctorService.getDoctors().subscribe(
      (response: Doctor[]) => {
        this.doctors = response;
        },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
      )
  }

  private getCurrentDoctor() {
    this.doctorService.getDoctorById(1).subscribe(
      (response: Doctor) => {
        this.currentDoctor = response;
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
    )
  }
}
