    <div class="container h-100" id = "main-container">
      <div class="row d-flex justify-content-center align-items-center h-100">
        <div class="col-12 col-md-9 col-lg-7 col-xl-6">
          <div class="card" style="border-radius: 15px;">
            <div class="card-body p-5">
              <h2 class="text-uppercase text-center mb-5">Welcome!</h2>
              <form>
                <div class="form-outline mb-2">
                  <input type="email" id="form3Example3cg" class="form-control form-control-lg"
                         ngModel required #email="ngModel" [ngModelOptions]="{standalone: true}"/>
                  <label class="form-label" for="form3Example3cg">Your Email</label>
                </div>

                <div class="form-outline mb-2">
                  <input type="password" id="form3Example4cg" class="form-control form-control-lg"
                         ngModel required #password="ngModel" [ngModelOptions]="{standalone: true}"/>
                  <label class="form-label" for="form3Example4cg">Password</label>
                </div>

                <div class="d-flex justify-content-center">
                  <button (click)="clickLogIn( email.value, password.value)"
                          type="button"
                          class="btn btn-success btn-block btn-lg gradient-custom-4 text-body">Log In</button>
                </div>

                <p class="text-center text-muted mt-5 mb-0">Is this your first time here?
                  <a  (click)="registerHere('Register')"
                      class="fw-bold text-body"><u>Register Here</u></a>
                </p>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
