import {Component, Injectable} from '@angular/core';
import { Router } from '@angular/router';
import {Client} from "../services/client";
import {ClientService} from "src/app/components/services/client.service";
import {HttpErrorResponse} from "@angular/common/http";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent {
  clients: Client[] = [];
  constructor(private router: Router,
              private clientService: ClientService) { }

  ngOnInit() {
    this.getClients();
  }

  public registerHere(pageName: string): void{
    this.router.navigate([`${pageName}`]);
  }

  public clickLogIn(verifyClientMail: String, verifyClientPassword : String, pageName: string): void{
    for(let client of this.clients){
      if (client.email == verifyClientMail)
        if (client.password == verifyClientPassword)
          this.router.navigate([`${pageName}`]);
    }
  }

  public getClients(){
    this.clientService.getClients().subscribe(
      (response: Client[]) => {
        this.clients = response;
      },
      (error: HttpErrorResponse) =>{
        alert(error.message);
      }
    );
  }
}
