import { Component } from '@angular/core';
import {Doctor} from "../services/doctor";
import {HttpErrorResponse} from "@angular/common/http";
import {DoctorService} from "../services/doctor.service";
import {ProfessionService} from "../services/profession.service";
import {toInteger} from "lodash";
import {Profession} from "../services/profession";
import {AppointmentService} from "../services/appointment.service";
import {RequirementsService} from "../services/requirements.service";
import {array} from "zod";

@Component({
  selector: 'app-schedule',
  templateUrl: './schedule.component.html',
  styleUrls: ['./schedule.component.css']
})

export class ScheduleComponent {
  public isBusy: string = "Free";
  public oneDoctor: Doctor | undefined;
  public oneProfession: Profession | undefined;

  public arrayCalendar: string[][];
  public arrayCalendarColor: string[][];
  public daysInWeek: number[];

  public free:string = "success";
  public busy:string = "danger";
  public waiting:string = "success";

  constructor(private doctorService: DoctorService,
              private professionService: ProfessionService,
              private appointService: AppointmentService,
              private requirementsService: RequirementsService){
    this.doctorService = doctorService;
    this.professionService = professionService;
    this.appointService = appointService;
    this.requirementsService = requirementsService;

    this.arrayCalendar = [];
    this.arrayCalendarColor = [];
    this.daysInWeek = [];
  }
  ngOnInit(){
    this.initCalendar();
    this.getOneDoctor();
    this.getOneProfession();
  }
  public refresh(): void{
    this.setCalendar();
  }

  public getNumberOfTheDay(day: string):number{
    if(day == "Monday")
      return 0;
    if(day == "Tuesday")
      return 1;
    if(day == "Wednesday")
      return 2;
    if(day == "Thursday")
      return 3;
    if(day == "Friday")
      return 4;
    else
      return 5;
  }

  public clickForSchedule(i: number, j: number): void{
    if(this.arrayCalendarColor[i][j] == this.busy)
      alert("This hour is ");
      console.log("do nothing");
  }

  public initCalendar():void {
    for (var i = 8; i < 16; i++) {
      this.arrayCalendarColor[i-8] = [];
      this.arrayCalendar[i - 8] = [];
      for (var j = 0; j < 6; j++) {
        this.arrayCalendar[i - 8][j] = "Free";
        this.arrayCalendarColor[i - 8][j] = this.free;
      }
    }
  }
  public setCalendar(): void {
    if(this.oneDoctor?.appointmentsDoctor != undefined) {
      for (let appointment of this.oneDoctor?.appointmentsDoctor) {
        this.arrayCalendar[appointment.hour-8][this.getNumberOfTheDay(appointment.day)] = "Busy";
        this.arrayCalendarColor[appointment.hour-8][this.getNumberOfTheDay(appointment.day)] = this.busy;
      }
    }
  }

  public getOneProfession(): void {
    this.professionService.getProfessionById(toInteger(localStorage.getItem("Profession"))).subscribe(
      (response: Profession) => {
        this.oneProfession = response;
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
    )
  }
  public getOneDoctor(): void{
    this.doctorService.getDoctorById(toInteger(localStorage.getItem("Doctor"))).subscribe(
      (response: Doctor) => {
        this.oneDoctor = response;
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
    )
  }

}
<section class="image vh-100 bg-image">
  <div class="container">
    <br>
    <br>
    <br>
    <div class="table-responsive">
      <table class="table table-bordered text-center">
        <thead>
        <tr class="bg-light-gray">
          <th ><button (click)="refresh()" type="button" class="btn btn-outline-dark">Refresh</button>
          </th>
          <th class="text-uppercase">Monday</th>
          <th class="text-uppercase">Tuesday</th>
          <th class="text-uppercase">Wednesday</th>
          <th class="text-uppercase">Thursday</th>
          <th class="text-uppercase">Friday</th>
          <th class="text-uppercase">Saturday</th>
        </tr>
        </thead>
        <tbody>
        <tr *ngFor="let nr of arrayCalendar; let i = index" [attr.data-index]="i" >
          <td class="align-middle bg-light-gray">0{{i+8}}:00</td>
          <td class = "bg-light-gray">
            <span ><button (click)="clickForSchedule(i, 0)" type="button" class="btn btn-outline-{{arrayCalendarColor[i][0]}}">{{nr[0]}}</button></span>
          </td>
          <td class = "bg-light-gray">
            <span ><button (click)="clickForSchedule(i, 1)" type="button" class="btn btn-outline-{{arrayCalendarColor[i][1]}}">{{nr[1]}}</button></span>
          </td>
          <td class = "bg-light-gray">
            <span ><button (click)="clickForSchedule(i, 2)" type="button" class="btn btn-outline-{{arrayCalendarColor[i][2]}}">{{nr[2]}}</button></span>
          </td>
          <td class = "bg-light-gray">
            <span ><button (click)="clickForSchedule(i, 3)" type="button" class="btn btn-outline-{{arrayCalendarColor[i][3]}}">{{nr[3]}}</button></span>
          </td>
          <td class = "bg-light-gray">
            <span ><button (click)="clickForSchedule(i, 4)" type="button" class="btn btn-outline-{{arrayCalendarColor[i][4]}}">{{nr[4]}}</button></span>
          </td>
          <td class = "bg-light-gray">
            <span ><button (click)="clickForSchedule(i, 5)" type="button" class="btn btn-outline-{{arrayCalendarColor[i][5]}}">{{nr[5]}}</button></span>
          </td>
        </tr>

        </tbody>
      </table>
    </div>
  </div>
</section>
