import { Component } from '@angular/core';
import { Router } from '@angular/router';
import {Client} from "../services/client";
import {HttpErrorResponse} from "@angular/common/http";
import {ClientService} from "../services/client.service";

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  constructor(private router: Router,
              private clientService: ClientService){ }

  ngOnInit() {
    this.getClients();
  }

  public registerAccount(pageName: string): void{
    this.router.navigate([`${pageName}`]);
  }

  public getClients(){
    this.clientService.getClients().subscribe(
      (response: Client[]) => {
        this.clients = response;
      },
      (error: HttpErrorResponse) =>{
        alert(error.message);
      }
    );
  }
}

<section class="image vh-100 bg-image">
  <div class="mask d-flex align-items-center h-100 gradient-custom-3">
    <div class="container h-100">
      <div class="row d-flex justify-content-center align-items-center h-100">
        <div class="col-12 col-md-9 col-lg-7 col-xl-6">
          <div class="card" style="border-radius: 15px;">
            <div class="card-body p-5">
              <h2 class="text-uppercase text-center mb-5">Welcome!</h2>
              <form>

                <div class="form-outline mb-2">
                  <input type="email" id="form3Example1cg" class="form-control form-control-lg" />
                  <label class="form-label" for="form3Example1cg">Your Name</label>
                </div>

                <div class="form-outline mb-2">
                  <input type="email" id="form3Example2cg" class="form-control form-control-lg" />
                  <label class="form-label" for="form3Example2cg">Your Email</label>
                </div>

                <div class="form-outline mb-2">
                  <input type="email" id="form3Example3cg" class="form-control form-control-lg" />
                  <label class="form-label" for="form3Example3cg">Your Age</label>
                </div>

                <div class="form-outline mb-2">
                  <input type="password" id="form3Example4cg" class="form-control form-control-lg" />
                  <label class="form-label" for="form3Example4cg">Type Password</label>
                </div>

                <div class="form-outline mb-2">
                  <input type="password" id="form3Example5cg" class="form-control form-control-lg" />
                  <label class="form-label" for="form3Example5cg">Repet Password</label>
                </div>

                <div class="d-flex justify-content-center">
                  <button (click)="registerAccount('login-component')"
                          type="button"
                          class="btn btn-success btn-block btn-lg gradient-custom-4 text-body">REGISTER</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
